import * as React from 'react';
import { View, StyleSheet } from 'react-native';
import { Button, Text } from 'react-native-paper';
import { useDispatch, useSelector } from 'react-redux';
import { validateEmail, validatePassword, validateRequired } from '../../validators/index';
import InputGroup from '../InputGroup/index';
import { useAlert } from '../../hooks/alert';

const ProfileForm = () => {

    const dispatch = useDispatch()

    const currentUser = useSelector((state) => state.currentUser.value);

    React.useEffect(() => {
        console.log(formData)
    }, [formData]);

    const { alert, setAlert, component: Snackbar } = useAlert()
    const [formData, setFormData] = React.useState({
        username: {
            isError: false,
            value: "",
            errors: [],
        },
        email: {
            isError: false,
            value: "",
            errors: []
        },
        password: {
            isError: false,
            value: "",
            errors: []
        }
    })

    React.useEffect(() => {
        handleValidateFormData(formData)
    }, [formData.email.value, formData.password.value])

    const handleSubmit = async () => {

        let message = "";
        let severity = "error"
        const data = {
            email: formData.email.value,
            password: formData.password.value
        }

        try {
            if (currentUser) {
                message = "Loged in with success";
                severity = "success";
            } else {
                throw new Error('missing token field in response call')
            }
        } catch (error) {
            message = "An unexpected error has been encountered, please try again later or contact tour administrator"
        } finally {
            setAlert({
                toggled: true,
                message,
                severity
            })
        }
    }

    const handleValidateFormData = (formData) => {
        const {
            isValid: usernameIsValid,
            errors: usernameValidationsErrors
        } = validateRequired("username", formData.username.value)
        const {
            isValid: emailIsValid,
            errors: emailValidationsErrors
        } = validateEmail("email", formData.email.value)
        const {
            isValid: passwordIsValid,
            errors: passwordValidationsErrors
        } = validatePassword("password", formData.password.value)

        setFormData({
            ...formData,
            username: {
                ...formData.username,
                isError: !usernameIsValid,
                errors: usernameValidationsErrors
            },
            email: {
                ...formData.email,
                isError: !emailIsValid,
                errors: emailValidationsErrors
            },
            password: {
                ...formData.password,
                isError: !passwordIsValid,
                errors: passwordValidationsErrors
            }
        })
    }

    const handleEmail = (email) => {
        const updatedFormData = {
            ...formData,
            email: {
                ...formData.email,
                value: email
            }
        }
        setFormData(updatedFormData)
    }


    const handlePassword = (password) => {
        const updatedFormData = {
            ...formData,
            password: {
                ...formData.password,
                value: password
            }
        }
        setFormData(updatedFormData)
    }

    const handleUsername = (username) => {
        const updatedFormData = {
            ...formData,
            username: {
                ...formData.username,
                value: username
            }
        }
        setFormData(updatedFormData)
    }


    const handleCloseAlert = () => {
        setAlert({
            toggled: false,
            message: "",
            severity: "error"
        })
    }

    return (
        <>
            <View style={styles.container}>
                <Text style={styles.header} variant="headlineMedium">User details</Text>

                <InputGroup
                    label={"Username"}
                    isError={formData.username.isError}
                    errors={formData.username.errors}
                    value={formData.username.value}
                    handleInput={username => handleUsername(username)}
                />

                <InputGroup
                    label={"Email"}
                    isError={formData.email.isError}
                    errors={formData.email.errors}
                    value={formData.email.value}
                    handleInput={email => handleEmail(email)}
                />

                <InputGroup
                    label={"Password"}
                    isError={formData.password.isError}
                    errors={formData.password.errors}
                    value={formData.password.value}
                    handleInput={password => handlePassword(password)}
                    secureTextEntry={true}
                />

                <Button disabled={formData.username.isError || formData.email.isError || formData.password.isError} mode="contained" onPress={handleSubmit}>
                    ok
                </Button>

            </View>

            <Snackbar onClose={handleCloseAlert} toggled={alert.toggled} message={alert.message} severity={alert.severity} />

        </>
    );
};

const styles = StyleSheet.create({
    header: {
        marginBottom: 24
    },
    container: {
        width: "100%",
        padding: 24
    },
});

export default ProfileForm;
