import * as React from 'react';
import { View, StyleSheet } from 'react-native';
import { getUserClocks } from '../../http/api';
import ClockInOutListItem from '../ClockInOutListItem';
import { useSelector } from 'react-redux';
import { Button } from 'react-native-paper';

const formatTimeToDate = (time) => {
    const date = new Date(time * 1000);
    return `${date.getMonth() + 1}/${date.getDate()}/${date.getFullYear()} at ${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`
}


const Dashboard = () => {

    const currentUser = useSelector((state) => state.currentUser.value)
    const [clocks, setClocks] = React.useState([])
    const [selectedFilter, setSelectedFilter] = React.useState(true)

    React.useEffect(() => console.log(clocks), [clocks])
    React.useEffect(() => {
        if (currentUser) {
            getUserClocks(currentUser.token, currentUser.user.id).then((userClocks) => setClocks(userClocks.data))
        }
    }, [currentUser])

    return (
        <>
            <View style={styles.filterContainer}>

                <Button style={{ marginRight: 8 }} mode="outlined" onPress={() => console.log('Pressed')}>
                    arrival
                </Button>
                <Button style={{ marginRight: 8 }} mode="outlined" onPress={() => console.log('Pressed')}>
                    departure
                </Button>
            </View>

            <View>
                {
                    clocks.filter((clock) => clock.status === selectedFilter).map((clock) => <ClockInOutListItem key={clock.id} title={formatTimeToDate(clock.time)} description={""} />)
                }
            </View>
        </>

    )
}

const styles = StyleSheet.create({
    filterContainer: {
        alignItems: "center",
        justifyContent: "start",
        display: "flex",
        flexDirection: "row"
    }
})

export default Dashboard
